$(function() {
  var btn = $('#show_examples')
      , method = btn.attr('href')
      , el = $('#code-examples')
      , langPicker = $('#lang-picker')
      ;
  
  var loadExample = function(lang) {
    if(typeof lang == 'undefined') { lang = ''; }
    
    el.find('#pre').html('<p>Loading...</p>');

    $.ajax({
      url: method
      , data: {
        lang: lang
      }
      , dataType: 'json'
      , success: function(data) {
        if(data['library'] && data['library']['url'] && data['library']['official']) {
          el.find('#lib-link')
            .show()
            .attr('href', data['library']['url'])
            .find('span').text(data['library']['displayName']);
        } else {
          el.find('#lib-link').hide();
        }

        if(data['success']) {
          el
            .find('#pre')
            .html('<pre></pre>')
              .find('pre')
              .attr('class', 'brush: ' + data['library']['brush'])
              .text(data['example']);

          SyntaxHighlighter.highlight({toolbar: false});
        } else {
          el
            .find('#pre')
            .html('<pre></pre>')
              .find('pre')
              .text(data['message']);
        }
      }
    });
  }

  btn.click(function(e) {
    e.preventDefault();

    if(el.is(':visible')) {
      el.slideUp();
      return false;
    }

    el
      .find('pre').empty().end()
      .slideDown();

    var lang = $('#lang').find('option:selected').val();
    loadExample(lang);
    
    return false;
  });
  
  langPicker.change(function() {
    var lang = $(this).find('option:selected').val();
    
    loadExample(lang);
  });
});
$(function() {
  var btn = $('#show_try')
      , submitBtn = $('#try-submit')
      , url = $('#try-pre-request')
      , inputs = $('#params input')
      , el = $('#try')
      , response = $('#try-response')
      ;

  btn.click(function(e) {
    e.preventDefault();

    if(el.is(':visible')) {
      btn.removeClass('active');
      el.slideUp();
      return false;
    }

    $('#try-pre-request').empty();
    btn.addClass('active');
    el.slideDown();
    
    calcUrl();

    return false;
  });
  
  var getParams = function() {
    var params = {};

    $('#params tbody tr').each(function(item) {
      var el = $(this)
          , input = el.find('td > input').length ? el.find('td > input') : el.find('td > select')
          , val = input.val()
          , isEmpty = !input.val()
          , isRequired = el.find('.required').length
          , isQs = el.find('.qs').length
          , isArray = el.find('.array_set').length
          ;

      if((isRequired || !isEmpty) && !isQs) {
        params[input.attr('name')] = ((typeof val == 'string') ? val : val.join('%2C'));
      }
      
      if(isArray) {
        el.find('.array_set').each(function() {
          var array_set = $(this);

          if(!params[array_set.data('key')]) { params[array_set.data('key')] = []; }
          
          var obj = {};
          array_set.find('input').each(function() {
            var array_set_input = $(this);

            obj[array_set_input.attr('name')] = array_set_input.val();
          });

          if(jQuery.map(obj, function(x) { return x == '' ? null : x; }).length) {
            params[array_set.data('key')].push(obj);
          }
        });
      }
    });

    return params;
  }

  var getQsParams = function() {
    var params = {};

    $('#params tbody tr').each(function(item) {
      var input = $(this).find('input')
          , isQs = $(this).find('.qs').length
          ;

      if(isQs) {
        params[input.attr('name')] = input.val();
      }
    });

    return params;
  }
  
  var calcUrl = function() {
    var url = $('#try-pre-request').data('url')
        , type = $('#try-pre-request').data('type').toUpperCase()
        , params = getParams()
        , qsParams = getQsParams()
        , qs = ''
        , postBody = ''
        ;

    // Inject normal params
    if(type == 'GET')  {

      for(key in params) {
        qs += ('&' + key + '={' + key + '}');
        qs = qs.replace('{' + key + '}', params[key] || ('{' + key + '}'));
      }    
      url += ('?' + qs.substr(1));
      
    } else {

      postBody += "\n<span class='key'>Content-Type:</span> <span class='string'>application/json</span> \n";
      postBody += "\n";
      postBody += syntaxHighlight(printJson(params));

    }

    // Inject querystring params
    for(key in qsParams) {
      url = url.replace('{' + key + '}', qsParams[key] || ('{' + key + '}'));
    }

    $('#try-pre-request')
      .html('<span class="key">' + type + '</span> <span class="string">' + url + "</span>" + postBody)
      .data('calc-url', url);
  }
  
  $(document).on('keyup', '#params input', function() {
    calcUrl();
  });

  $(document).on('change', '#params select', function() {
    calcUrl();
  });
  
  submitBtn.click(function(e) {
    e.preventDefault();
    
    // Make sure we have all required params
    var isMissing = false;
    $('#params tbody tr').each(function(item) {
      var isRequired = $(this).find('.required').length
          , isDisabled = $(this).find('input').is(':disabled')
          , val = $(this).find('input').val()
          ;
      
      if(isRequired && !isDisabled && !val) {
        isMissing = $(this).find('input').attr('name');
      }
    });
    
    if(isMissing) {
      alert('Missing required parameter: ' + isMissing);
      return false;
    }
    
    var data = {
      url: url.data('calc-url')
      , type: url.data('type')
      , params: getParams()
    };
    
    $('#try-pre-response').empty().addClass('loading');

    $.ajax({
      url: '/dev/try'
      , type: 'POST'
      , dataType: 'text'
      , data: data
      , success: function(data) {
        $('#try-pre-response').removeClass('loading').html(syntaxHighlight(data));
      }
    })

    return false;
  });
  
  $('#try-reset').click(function(e) {
    e.preventDefault();
    
    $('#params tbody tr').each(function(item) {
      $(this).find('input').val('');
    });

    $('#try-pre-response').text('(click Submit to query the API)');
    calcUrl();
    
    return false;
  });
  
  $(document).on("click", ".array_delete", function() {
    var array_set = $(this).parent('.array_set');

    if(array_set.siblings('.array_set').length) {
      array_set.remove();
    }

    calcUrl();
  });
  
  $(document).on("click", ".array_add", function() {
    var last_array_set = $(this).prev('.array_set');
    last_array_set.clone().find('input').val('').end().insertAfter(last_array_set);
  });
});
$(function() {
    
  $(".toggle_response_params a").click(function() {
    var el = $(this);
    $('tr.response_param').toggle();

    if(el.data('state') == 'closed') {
      el
        .data('state', 'open')
        .html('<i class="icon-remove">&nbsp;</i>&nbsp;&nbsp;Hide Response Parameters');
    } else {
      el
        .data('state', 'closed')
        .html('<i class="icon-plus">&nbsp;</i>&nbsp;&nbsp;Show Response Parameters');
    }
  });

  if(typeof _response_params_info !== 'undefined') {
    for(key in _response_params_info) {
      $("span.key:contains('\"" + key + "\"')").next().after('<a href="' + _response_params_info[key] + '" class="param_help" rel="tooltip" title="More info about the ' + key + ' parameter"><i class="icon-question-sign icon-faded">&nbsp;</i></a>');
    }
    
  	// Activate tooltips
  	$("[rel=tooltip]").tooltip();
  }
})
