$(function() {
  initCodeContainers();

  // Initialize highlighting via highlight.js
  hljs.initHighlighting();
});

Object.values = function (obj) {
    var vals = [];
    for( var key in obj ) {
        if ( obj.hasOwnProperty(key) ) {
            vals.push(obj[key]);
        }
    }
    return vals;
};

function Snippet(snippetElement) {
  this.language = $(snippetElement).find('code')[0].className.split(" ")[0].split('-')[1];
  this.element = snippetElement;

  // inject language watermark
  var extensions = {
    'ruby': '.rb',
    'python': '.py',
    'js': '.js',
    'php': '.php',
    'html': '.html'
  };
  var extension = extensions[this.language] || this.language;
  $(this.element).append($('<div class="languageWatermark">' + extension + '</div>'));
}

function CodeContainer(containerElement) {
  var that = this;

  this.element = containerElement;
  this.snippets = {};
  this.buttons = [];

  // initialize snippets:
  $(containerElement).children('pre').each(function(index, snippet) {
    var snip = new Snippet(snippet);
    that.snippets[snip.language] = snip;
  });

  // kill any empty p's resulting from whitespace:
  $(containerElement).children('p').remove();
}

CodeContainer.prototype.hideAll = function() {
  Object.values(this.snippets).forEach(function(snippet) {
    $(snippet.element).hide();
  });
};

CodeContainer.prototype.showLang = function(language) {
  this.hideAll();
  if (language in this.snippets) {
    $(this.snippets[language].element).show();
  }
  else {
    // show first snippet if lang not found
    $(Object.values(this.snippets)[0].element).show();
  }
};

CodeContainer.prototype.addButtons = function() {
  var that = this;

  var buttonContainer = $('<div class="buttonContainer"></div>');

  Object.values(this.snippets).forEach(function(snippet) {
    var button = $('<button class="chooseLang" value="' + snippet.language + '">' + snippet.language + '</button>');
    button.on('click', function(e) {
      that.showLang(e.target.value);
    });
    buttonContainer.append(button);
  });

  $(this.element).prepend(buttonContainer);
};

var initCodeContainers = function() {
  var containers = [];

  $('.codeContainer').each(function(index, container) {
    console.log('yay', container);

    var c = new CodeContainer(container);
    containers.push(c);

    // hide all langs but one
    c.hideAll();

    // show ruby by default
    c.showLang('ruby');
    
    // add language switch buttons
    c.addButtons();
  });
};
